import React from 'react';
import { Container, Image } from '../StyledComponents/StyledComponents';
import coffeeData from '../../data/coffeeData';

const CoffeeOfTheDay = () => {
    return (
        <Container>
            <Image src={coffeeData.image} alt={coffeeData.name} />
            <h2>{coffeeData.name}</h2>
            <p>{coffeeData.description}</p>
            
            <h3>Recipe</h3>
            <ul>
                <li>Grind: {coffeeData.recipe.grind}</li>
                <li>Water: {coffeeData.recipe.water}</li>
                <li>Temperature: {coffeeData.recipe.temperature}</li>
                <li>Brew Time: {coffeeData.recipe.brewTime}</li>
                <li>Ratio: {coffeeData.recipe.ratio}</li>
            </ul>

            <h3>Tasting Notes</h3>
            <ul>
                {coffeeData.tastingNotes.map((note, index) => (
                    <li key={index}>{note}</li>
                ))}
            </ul>

            <h3>Origin</h3>
            <p>{coffeeData.origin}</p>
            <p>Elevation: {coffeeData.elevation}</p>
            <p>Process: {coffeeData.process}</p>
        </Container>
    );
};

export default CoffeeOfTheDay;
