import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Container } from '../StyledComponents/StyledComponents';
import axios from 'axios';
import { useHistory } from 'react-router-dom';

const PlantImage = styled.img`
    width: 100%;
    max-width: 600px;
    height: auto;
    border-radius: 8px;
    margin: 1rem 0;
    object-fit: cover;
`;

const PlantInfo = styled.div`
    margin: 1rem 0;
    padding: 1rem;
    background-color: ${props => props.theme.colors.lightGray};
    border-radius: 8px;
`;

const PlantDetail = styled.div`
    margin: 1rem 0;

    strong {
        color: ${props => props.theme.colors.primary};
    }
`;

const LoadingContainer = styled(Container)`
    text-align: center;
    padding: 2rem;
`;

const ErrorContainer = styled(Container)`
    text-align: center;
    padding: 2rem;
    color: red;
`;

const NextButton = styled.button`
    background-color: #333;
    color: white;
    padding: 0.8rem 1.5rem;
    border: none;
    border-radius: 4px;
    font-size: 1rem;
    cursor: pointer;
    margin-top: 1rem;
    transition: background-color 0.3s;

    &:hover {
        background-color: #555;
    } 
`;

const PlantOfTheDay = () => {
    const [plant, setPlant] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [clientToken, setClientToken] = useState(null);
    const [currentPlantIndex, setCurrentPlantIndex] = useState(0);
    const [plants, setPlants] = useState([]);
    const history = useHistory();
    const TOKEN = 'YPL0oCPPmylIEwr78weBx3uvF4s3qv2k4YnjfaBC53M';

    // First, get a client-side token
    useEffect(() => {
        const getClientToken = async () => {
            try {
                const params = {
                    origin: window.location.origin,
                    token: TOKEN
                };

                const response = await axios({
                    method: 'post',
                    url: '/api/auth/claim',
                    data: params,
                    headers: { 
                        'Content-Type': 'application/json',
                        'Accept': 'application/json'
                    }
                });

                if (response.data && response.data.token) {
                    setClientToken(response.data.token);
                } else {
                    throw new Error('No token received');
                }
            } catch (err) {
                console.error('Error getting client token:', err);
                setError('Failed to authenticate with the plant database');
                setLoading(false);
            }
        };

        getClientToken();
    }, []);

    // Then fetch the plant data once we have the client token
    useEffect(() => {
        const fetchPlant = async (attemptedIds = new Set()) => {
            if (!clientToken) return;

            try {

                // Get a random plant ID between 1 and 10000
                let plantId = Math.floor(Math.random() * 10000) + 1;

                // If this ID has been attempted, try another one
                while (attemptedIds.has(plantId)) {
                    plantId = Math.floor(Math.random() * 10000) + 1;
                }
                attemptedIds.add(plantId);

                const response = await axios.get(
                    `/api/v1/species/${plantId}?token=${clientToken}`,
                    {
                        headers: {
                            'Accept': 'application/json'
                        }
                    }
                );

                if (response.data && response.data.data) {
                    // If the plant has no image and we haven't tried 5 times, try another plant
                    if (!response.data.data.image_url && attemptedIds.size < 5) {
                        return fetchPlant(attemptedIds);
                    }
                    setPlants(prevPlants => [...prevPlants, response.data.data]);
                    setPlant(response.data.data);
                } else {
                    throw new Error('Invalid plant data received');
                }
            } catch (err) {
                console.error('Error fetching plant:', err);
                // If we haven't tried 5 times and got an error, try another plant
                if (attemptedIds.size < 5) {
                    return fetchPlant(attemptedIds);
                }
                setError(err.message);
                setLoading(false);
            }
        };

        fetchPlant();
    }, [clientToken]);

    const handleNext = () => {
        if (plants.length > 0) {
            setCurrentPlantIndex((prevIndex) => (prevIndex + 1) % plants.length);
        }
    };

    if (loading) {
        return (
            <PlantContainer>
                <h2>Loading plant data...</h2>
            </PlantContainer>
        );
    }

    if (error) {
        return (
            <PlantContainer>
                <h2>Error: {error}</h2>
            </PlantContainer>
        );
    }

    if (!plants.length) {
        return (
            <PlantContainer>
                <h2>No plants available</h2>
            </PlantContainer>
        );
    }

    const currentPlant = plants[currentPlantIndex];

    return (
        <PlantContainer>
            <h2>{currentPlant.common_name || currentPlant.scientific_name}</h2>

            {currentplant.image_url && (
                <PlantImage src={currentplant.image_url} alt={currentplant.common_name || currentplant.scientific_name} />
            )}

            <PlantInfo>
                <PlantDetail>
                    <strong>Family:</strong> {currentplant.family_common_name}
                </PlantDetail>
                {currentplant.year && (
                    <PlantDetail>
                        <strong>Year Discovered:</strong> {currentplant.year}
                    </PlantDetail>
                )}
                {currentplant.bibliography && (
                    <PlantDetail>
                        <strong>Origin:</strong> {currentplant.distribution.native}
                    </PlantDetail>
                )}
            </PlantInfo>
            <NextButton onClick={handleNext}>Next Plant</NextButton>
        </PlantContainer>
    );
};

export default PlantOfTheDay;
