import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import drinksData from '../../data/drinksData';

const DrinkContainer = styled.div`
    max-width: 800px;
    margin: 4rem auto;
    padding: 2rem;
    background: white;
    border-radius: 10px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const DrinkImage = styled.img`
    width: 100%;
    max-height: 400px;
    object-fit: cover;
    border-radius: 8px;
    margin-bottom: 1.5rem;
`;

const DrinkTitle = styled.h2`
    color: #2c3e50;
    margin-bottom: 1rem;
    font-size: 2rem;
`;

const DrinkDescription = styled.p`
    color: #34495e;
    margin-bottom: 1.5rem;
    line-height: 1.6;
`;

const RecipeSection = styled.div`
    margin: 2rem 0;
`;

const SectionTitle = styled.h3`
    color: #2c3e50;
    margin-bottom: 1rem;
    font-size: 1.5rem;
`;

const List = styled.ul`
    list-style-type: none;
    padding: 0;
`;

const ListItem = styled.li`
    margin-bottom: 0.5rem;
    color: #34495e;
    padding-left: 1.5rem;
    position: relative;

    &:before {
        content: "•";
        color: #e74c3c;
        position: absolute;
        left: 0;
    }
`;

const NavigationButtons = styled.div`
    display: flex;
    justify-content: space-between;
    margin-top: 2rem;
`;

const NavButton = styled.button`
    padding: 0.5rem 1rem;
    background-color: #e74c3c;
    color: white;
    border: none;
    border-radius: 5px;
    cursor: pointer;
    font-size: 1rem;
    transition: background-color 0.3s;

    &:hover {
        background-color: #c0392b;
    }

    &:disabled {
        background-color: #95a5a6;
        cursor: not-allowed;
    }
`;

const CoffeeOfTheDay = () => {
    const [currentDrinkIndex, setCurrentDrinkIndex] = useState(0);
    const [currentDrink, setCurrentDrink] = useState(null);

    useEffect(() => {
        setCurrentDrink(drinksData[currentDrinkIndex]);
    }, [currentDrinkIndex]);

    const handleNext = () => {
        setCurrentDrinkIndex((prevIndex) => 
            prevIndex === drinksData.length - 1 ? 0 : prevIndex + 1
        );
    };

    const handlePrevious = () => {
        setCurrentDrinkIndex((prevIndex) => 
            prevIndex === 0 ? drinksData.length - 1 : prevIndex - 1
        );
    };

    if (!currentDrink) return null;

    return (
        <DrinkContainer>
            <DrinkImage src={currentDrink.image} alt={currentDrink.name} />
            <DrinkTitle>{currentDrink.name}</DrinkTitle>
            <DrinkDescription>{currentDrink.description}</DrinkDescription>
            
            <RecipeSection>
                <SectionTitle>Ingredients</SectionTitle>
                <List>
                    {currentDrink.recipe.ingredients.map((ingredient, index) => (
                        <ListItem key={index}>{ingredient}</ListItem>
                    ))}
                </List>
            </RecipeSection>

            <RecipeSection>
                <SectionTitle>Steps</SectionTitle>
                <List>
                    {currentDrink.recipe.steps.map((step, index) => (
                        <ListItem key={index}>{step}</ListItem>
                    ))}
                </List>
            </RecipeSection>

            <RecipeSection>
                <SectionTitle>Tasting Notes</SectionTitle>
                <List>
                    {currentDrink.tastingNotes.map((note, index) => (
                        <ListItem key={index}>{note}</ListItem>
                    ))}
                </List>
            </RecipeSection>

            <RecipeSection>
                <SectionTitle>Variations</SectionTitle>
                <List>
                    {currentDrink.variations.map((variation, index) => (
                        <ListItem key={index}>{variation}</ListItem>
                    ))}
                </List>
            </RecipeSection>

            <NavigationButtons>
                <NavButton onClick={handlePrevious}>Previous</NavButton>
                <NavButton onClick={handleNext}>Next</NavButton>
            </NavigationButtons>
        </DrinkContainer>
    );
};

export default CoffeeOfTheDay;
