import React, { useState, useEffect } from 'react';
import { Container } from '../StyledComponents/StyledComponents';
import styled from 'styled-components';
import axios from 'axios';

const PlantContainer = styled(Container)`
    text-align: center;
    max-width: 800px;
    margin: 0 auto;
    padding: 2rem;
    width: 100%;
    box-sizing: border-box;
    position: relative;

    @media (max-width: ${props => props.theme.queries.mobile}) {
        padding: 1rem;
    }
`;

const PlantImage = styled.img`
    max-width: 100%;
    height: auto;
    border-radius: 8px;
    margin: 2rem auto;
    display: block;
`;

const DateDisplay = styled.p`
    text-transform: uppercase;
    letter-spacing: 2px;
    font-size: 14px;
    color: ${props => props.theme.body.text};
`;

const RecipeSection = styled.div`
    margin: 2rem auto;
    text-align: left;
    max-width: 600px;
`;

const Badge = styled.span`
    display: inline-block;
    padding: 0.25rem 0.75rem;
    margin: 0.25rem;
    border-radius: 4px;
    background-color: rgba(255, 255, 255, 0.1);
    font-size: 14px;
`;

const LoadingState = styled.div`
    text-align: center;
    padding: 2rem;
    color: ${props => props.theme.body.text};
`;

const ErrorState = styled.div`
    text-align: center;
    padding: 2rem;
    color: red;
`;

const PlantOfTheDay = () => {
    const [plant, setPlant] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const API_KEY = 'sk-qAtT68009cea5a0689850';
    const MAX_RETRIES = 3;
    const RETRY_DELAY = 2000; // 2 seconds

    // Get a deterministic "random" number for today
    const getDailyRandomNumber = () => {
        const today = new Date();
        const dateString = `${today.getFullYear()}-${today.getMonth()}-${today.getDate()}`;
        let hash = 0;
        for (let i = 0; i < dateString.length; i++) {
            const char = dateString.charCodeAt(i);
            hash = ((hash << 5) - hash) + char;
            hash = hash & hash;
        }
        return Math.abs(hash);
    };

    const fetchPlantWithRetry = async (retryCount = 0) => {
        try {
            // Check if we have cached data for today
            const cachedData = localStorage.getItem('plantOfTheDay');
            const cachedDate = localStorage.getItem('plantDate');
            const today = new Date().toDateString();

            if (cachedData && cachedDate === today) {
                setPlant(JSON.parse(cachedData));
                setLoading(false);
                return;
            }

            // Use today's date to generate a consistent page and index
            const dailyRandom = getDailyRandomNumber();
            const randomPage = (dailyRandom % 10) + 1;
            
            const response = await axios.get(
                `https://perenual.com/api/v2/species-list?key=${API_KEY}&page=${randomPage}&indoor=1`
            );

            if (response.data.data && response.data.data.length > 0) {
                const randomIndex = dailyRandom % response.data.data.length;
                const randomPlant = response.data.data[randomIndex];
                
                const detailResponse = await axios.get(
                    `https://perenual.com/api/v2/species/details/${randomPlant.id}?key=${API_KEY}`
                );

                // Cache the result
                localStorage.setItem('plantOfTheDay', JSON.stringify(detailResponse.data));
                localStorage.setItem('plantDate', today);
                
                setPlant(detailResponse.data);
                setLoading(false);
            }
        } catch (err) {
            console.error(`Attempt ${retryCount + 1} failed:`, err);
            
            if (retryCount < MAX_RETRIES) {
                setTimeout(() => {
                    fetchPlantWithRetry(retryCount + 1);
                }, RETRY_DELAY);
            } else {
                setError('Failed to fetch plant data after multiple attempts. Please try again later.');
                setLoading(false);
            }
        }
    };

    useEffect(() => {
        fetchPlantWithRetry();
    }, []);

    const today = new Date().toLocaleDateString('en-US', {
        weekday: 'long',
        month: 'long',
        day: 'numeric',
        year: 'numeric'
    });

    if (loading) {
        return (
            <PlantContainer>
                <LoadingState>Loading plant of the day...</LoadingState>
            </PlantContainer>
        );
    }

    if (error) {
        return (
            <PlantContainer>
                <ErrorState>{error}</ErrorState>
            </PlantContainer>
        );
    }

    if (!plant) {
        return (
            <PlantContainer>
                <ErrorState>No plant data available.</ErrorState>
            </PlantContainer>
        );
    }

    return (
        <PlantContainer>
            <DateDisplay>Today's Plant â€¢ {today}</DateDisplay>
            {plant.default_image && (
                <PlantImage 
                    src={plant.default_image.regular_url} 
                    alt={plant.common_name} 
                />
            )}
            <h2>{plant.common_name}</h2>
            <p>{plant.description}</p>

            <RecipeSection>
                <h3>Care Instructions</h3>
                <ul>
                    {plant.watering && <li>Watering: {plant.watering}</li>}
                    {plant.sunlight && <li>Sunlight: {Array.isArray(plant.sunlight) ? plant.sunlight.join(', ') : plant.sunlight}</li>}
                    {plant.pruning && <li>Pruning: {plant.pruning}</li>}
                </ul>
            </RecipeSection>

            <div>
                <h3>Growing Conditions</h3>
                {plant.hardiness && <Badge>Hardiness: {plant.hardiness.min} - {plant.hardiness.max}</Badge>}
                {plant.watering && <Badge>{plant.watering}</Badge>}
                {plant.sunlight && Array.isArray(plant.sunlight) && 
                    plant.sunlight.map((light, index) => (
                        <Badge key={index}>{light}</Badge>
                    ))
                }
            </div>

            {plant.origin && (
                <RecipeSection>
                    <h3>Origin</h3>
                    <p>{Array.isArray(plant.origin) ? plant.origin.join(', ') : plant.origin}</p>
                </RecipeSection>
            )}
        </PlantContainer>
    );
};

export default PlantOfTheDay;
