import React, { useState, useEffect } from 'react';
import { Container } from '../StyledComponents/StyledComponents';
import styled from 'styled-components';
import { useHistory } from 'react-router-dom';
import axios from 'axios';

const PlantContainer = styled(Container)`
    text-align: center;
    max-width: 800px;
    margin: 0 auto;
    padding: 2rem;
    width: 100%;
    box-sizing: border-box;
    position: relative;

    @media (max-width: ${props => props.theme.queries.mobile}) {
        padding: 1rem;
    }

    h2 {
        margin-top: 1rem;
    }

    h3 {
        margin-top: 2rem;
    }

    p {
        margin: 1rem auto;
        max-width: 600px;
    }
`;

const PlantImage = styled.img`
    max-width: 35%;
    max-height: 35%;
    border-radius: 8px;
    margin: 2rem auto;
    display: block;
    object-fit: fill;
`;

const CareSection = styled.div`
    margin: 2rem auto;
    text-align: left;
    max-width: 600px;
`;

const CareList = styled.ul`
    list-style-type: none;
    padding: 0;
    margin: 1rem 0;
`;

const CareItem = styled.li`
    margin: 0.5rem 0;
    padding-left: 1.5rem;
    position: relative;

    &:before {
        content: "•";
        position: absolute;
        left: 0;
        color: #333;
    }
`;

const NextButton = styled.button`
    background-color: #333;
    color: white;
    padding: 0.8rem 1.5rem;
    border: none;
    border-radius: 4px;
    font-size: 1rem;
    cursor: pointer;
    margin-top: 1rem;
    transition: background-color 0.3s;

    &:hover {
        background-color: #555;
    }
`;

const PlantOfTheDay = () => {
    const [currentPlantIndex, setCurrentPlantIndex] = useState(0);
    const [plants, setPlants] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [clientToken, setClientToken] = useState(null);
    const history = useHistory();
    const TOKEN = 'YPL0oCPPmylIEwr78weBx3uvF4s3qv2k4YnjfaBC53M';

    useEffect(() => {
        const fetchClientToken = async () => {
            try {
                const response = await axios.get('https://perenual.com/api/auth/token', {
                    headers: {
                        'Authorization': `Bearer ${TOKEN}`
                    }
                });
                setClientToken(response.data.token);
            } catch (err) {
                console.error('Error fetching client token:', err);
                setError('Failed to authenticate with plant API');
                setLoading(false);
            }
        };

        fetchClientToken();
    }, []);

    useEffect(() => {
        const fetchPlants = async (attemptedIds = new Set()) => {
            if (!clientToken) return;

            try {
                // Get a random plant ID between 1 and 10000
                let plantId = Math.floor(Math.random() * 10000) + 1;

                // If this ID has been attempted, try another one
                while (attemptedIds.has(plantId)) {
                    plantId = Math.floor(Math.random() * 10000) + 1;
                }
                attemptedIds.add(plantId);

                const response = await axios.get(
                    `/api/v1/species/${plantId}?token=${clientToken}`,
                    {
                        headers: {
                            'Accept': 'application/json'
                        }
                    }
                );

                if (response.data && response.data.data) {
                    // If the plant has no image and we haven't tried 5 times, try another plant
                    if (!response.data.data.image_url && attemptedIds.size < 5) {
                        return fetchPlants(attemptedIds);
                    }
                    setPlants(prevPlants => [...prevPlants, response.data.data]);
                    setLoading(false);
                } else {
                    throw new Error('Invalid plant data received');
                }
            } catch (err) {
                console.error('Error fetching plant:', err);
                // If we haven't tried 5 times and got an error, try another plant
                if (attemptedIds.size < 5) {
                    return fetchPlants(attemptedIds);
                }
                setError(err.message);
                setLoading(false);
            }
        };

        fetchPlants();
    }, [clientToken]);

    const handleNext = () => {
        if (plants.length > 0) {
            setCurrentPlantIndex((prevIndex) => (prevIndex + 1) % plants.length);
        }
    };

    if (loading) {
        return (
            <PlantContainer>
                <h2>Loading plant data...</h2>
            </PlantContainer>
        );
    }

    if (error) {
        return (
            <PlantContainer>
                <h2>Error: {error}</h2>
            </PlantContainer>
        );
    }

    if (!plants.length) {
        return (
            <PlantContainer>
                <h2>No plants available</h2>
            </PlantContainer>
        );
    }

    const currentPlant = plants[currentPlantIndex];

    return (
        <PlantContainer>
            <h2>{currentPlant.common_name || currentPlant.scientific_name}</h2>

            {currentPlant.image_url && (
                <PlantImage src={currentPlant.image_url} alt={currentPlant.common_name || currentPlant.scientific_name} />
            )}

            <CareSection>
                <h3>Plant Information</h3>
                <CareList>
                    {currentPlant.family_common_name && (
                        <CareItem>Family: {currentPlant.family_common_name}</CareItem>
                    )}
                    {currentPlant.year && (
                        <CareItem>Year Discovered: {currentPlant.year}</CareItem>
                    )}
                    {currentPlant.distribution?.native && (
                        <CareItem>Origin: {currentPlant.distribution.native}</CareItem>
                    )}
                </CareList>
            </CareSection>

            <NextButton onClick={handleNext}>Next Plant</NextButton>
        </PlantContainer>
    );
};

export default PlantOfTheDay;
