import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { BodyContainer, Container } from '../components/StyledComponents/StyledComponents';

const Post = () => {
    const { id } = useParams();
    const [post, setPost] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/posts/${id}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch post');
                }
                const data = await response.json();
                setPost(data);
            } catch (err) {
                console.error('Error fetching post:', err);
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchPost();
    }, [id]);

    if (loading) {
        return (
            <BodyContainer>
                <Container top={6}>
                    <div>Loading...</div>
                </Container>
            </BodyContainer>
        );
    }

    if (error) {
        return (
            <BodyContainer>
                <Container top={6}>
                    <div>Error: {error}</div>
                </Container>
            </BodyContainer>
        );
    }

    if (!post) {
        return (
            <BodyContainer>
                <Container top={6}>
                    <div>Post not found</div>
                </Container>
            </BodyContainer>
        );
    }

    return (
        <BodyContainer>
            <Container top={6}>
                <Container leftAlign bottom={4}>
                    <h1>{post.title}</h1>
                    <p style={{ color: '#666', marginTop: '1rem' }}>
                        {new Date(post.createdAt).toLocaleDateString()}
                    </p>
                </Container>
                {post.coverImage && (
                    <Container bottom={4}>
                        <img 
                            src={post.coverImage} 
                            alt={post.title}
                            style={{ 
                                width: '100%', 
                                maxWidth: '800px', 
                                borderRadius: '4px',
                                margin: '0 auto',
                                display: 'block'
                            }} 
                        />
                    </Container>
                )}
                <Container leftAlign>
                    <div style={{ 
                        lineHeight: '1.6',
                        fontSize: '1.1rem',
                        whiteSpace: 'pre-wrap'
                    }}>
                        {post.content}
                    </div>
                </Container>
            </Container>
        </BodyContainer>
    );
};

export default Post;
