import React, { Component } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter as Router, Routes, Route, Navigate } from "react-router-dom";

// Pages
import Home from "./pages/Home.jsx";
import Coffee from "./pages/Coffee.jsx";
import Plant from "./pages/Plant.jsx";
import Write from "./pages/Write.jsx";
import Login from "./pages/Login.jsx";

import BlogPost from './components/BlogPost/BlogPost';

import * as serviceWorker from "./serviceWorker";
import { AnimatePresence } from "framer-motion";
import { GlobalStyle } from "./components/StyledComponents/StyledComponents.jsx";
import { ThemeProvider } from "styled-components";
import theme from "./components/StyledComponents/Theme";

import Posts from '../src/posts/Posts';

// Google Analytics
import ReactGA from "react-ga";

// Protected Route component
const ProtectedRoute = ({ children }) => {
  const userRole = localStorage.getItem('userRole');
  if (!userRole) {
    return <Navigate to="/login" replace />;
  }
  return children;
};

// Admin Protected Route component
const AdminProtectedRoute = ({ children }) => {
  const userRole = localStorage.getItem('userRole');
  if (userRole !== 'admin') {
    return <Navigate to="/" replace />;
  }
  return children;
};

class App extends Component {

    render () {

        if(window.location.hostname !== "localhost") {
            
            // Google Analytics ID goes here.
            let trackingId = "123";

            ReactGA.initialize(trackingId);
            ReactGA.pageview('/home');
        }


        return (
            <ThemeProvider theme={theme}>
                <GlobalStyle />
                <Router>
                    <Routes>
                        <Route path="/login" element={<Login />} />
                        <Route path="/" element={
                            <ProtectedRoute>
                                <App />
                            </ProtectedRoute>
                        }>
                            <Route index element={<Home />} />
                            <Route path="coffee" element={<Coffee />} />
                            <Route path="plant" element={<Plant />} />
                            <Route path="write" element={
                                <AdminProtectedRoute>
                                    <Write />
                                </AdminProtectedRoute>
                            } />
                        </Route>
                    </Routes>
                </Router>
            </ThemeProvider>
        )
    }
}

serviceWorker.unregister();
ReactDOM.render(<App />, document.getElementById('root'))
