import React from 'react';
import { useHistory } from 'react-router-dom';
import { 
  HomeContainer, 
  HeroSection, 
  HeroTitle, 
  HeroSubtitle, 
  HeroButton,
  FeaturedArticlesSection,
  AboutSection,
  AboutTitle,
  AboutContent,
  AboutImage,
  AboutText,
  AboutButton
} from './Home.styles';
import FeaturedArticles from '../components/FeaturedArticles/FeaturedArticles';

const Home = () => {
  const history = useHistory();

  return (
    <HomeContainer>
      <HeroSection>
        <HeroTitle>Welcome to Our Blog</HeroTitle>
        <HeroSubtitle>Discover stories, ideas, and inspiration</HeroSubtitle>
        <HeroButton onClick={() => history.push('/blog')}>Explore Blog</HeroButton>
      </HeroSection>

      <FeaturedArticlesSection>
        <FeaturedArticles />
      </FeaturedArticlesSection>

      <AboutSection>
        <AboutTitle>About Us</AboutTitle>
        <AboutContent>
          <AboutImage src="https://images.unsplash.com/photo-1499750310107-5fef28a66643?ixlib=rb-1.2.1&auto=format&fit=crop&w=1350&q=80" alt="About Us" />
          <AboutText>
            <p>Welcome to our blog! We're passionate about sharing knowledge, stories, and experiences with our readers.</p>
            <p>Our team of writers covers a wide range of topics, from technology and science to lifestyle and culture.</p>
            <p>Join us on this journey of discovery and learning.</p>
          </AboutText>
        </AboutContent>
        <AboutButton onClick={() => history.push('/about')}>Learn More</AboutButton>
      </AboutSection>
    </HomeContainer>
  );
};

export default Home;
