import React from 'react';
import {
    BodyContainer,
    Container,
    HeroContainer
} from '../components/StyledComponents/StyledComponents.jsx';
import Navigation from '../components/Navigation/Navigation.jsx';
import CoffeeOfTheDay from '../components/Coffee/CoffeeOfTheDay.jsx';
import Footer from '../components/Footer/Footer.jsx';
import Link from '../components/Utility/Link.jsx';
import Data from '../Data.js';

const Coffee = () => {
    return (
        <>
            <Navigation page="coffee" />
            <BodyContainer>
                <HeroContainer
                    animate={{ opacity: 1 }}
                    initial={{ opacity: 0 }}
                    transition={{ duration: 2, delay: 1 }}
                    exit={{ opacity: 0 }}
                >
                    <Container flex top={6}>
                        <Container width={50} bottom={6} right={2}>
                            <h1>{Data.coffee.title}</h1>
                            <p>{Data.coffee.description} <nobr><Link external text={Data.coffee.email} route={`mailto:${Data.coffee.email}`} />.</nobr></p>
                        </Container>
                        <Container width={50}>
                            <coffeeOfTheDay />
                        </Container>
                    </Container>
                </HeroContainer>
            </BodyContainer>
            <Footer />
        </>
    );
}

export default Coffee;
