import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { Container } from '../StyledComponents/StyledComponents';
import styled from 'styled-components';

const WriteContainer = styled(Container)`
    max-width: 800px;
    margin: 0 auto;
    padding: 2rem;
`;

const TitleInput = styled.input`
    width: 100%;
    padding: 1rem;
    font-size: 2rem;
    background: transparent;
    border: none;
    border-bottom: 1px solid ${props => props.theme.colors.gray};
    color: ${props => props.theme.body.headers};
    margin-bottom: 2rem;
    font-family: ${props => props.theme.typography.font};

    &:focus {
        outline: none;
        border-bottom-color: ${props => props.theme.body.headers};
    }
`;

const ContentTextarea = styled.textarea`
    width: 100%;
    min-height: 500px;
    padding: 1rem;
    font-size: 1.1rem;
    line-height: 1.6;
    background: transparent;
    border: 1px solid ${props => props.theme.colors.gray};
    color: ${props => props.theme.body.text};
    border-radius: 4px;
    font-family: ${props => props.theme.typography.font};
    resize: vertical;

    &:focus {
        outline: none;
        border-color: ${props => props.theme.body.headers};
    }
`;

const ButtonGroup = styled.div`
    display: flex;
    gap: 1rem;
    margin-top: 2rem;
`;

const Button = styled.button`
    padding: 0.75rem 1.5rem;
    border: none;
    border-radius: 4px;
    font-size: 1rem;
    cursor: pointer;
    transition: all 0.2s ease;
    font-family: ${props => props.theme.typography.font};

    &:hover {
        transform: translateY(-2px);
    }

    &:active {
        transform: translateY(0);
    }
`;

const PublishButton = styled(Button)`
    background: ${props => props.theme.body.headers};
    color: ${props => props.theme.colors.black};
`;

const SaveDraftButton = styled(Button)`
    background: transparent;
    border: 1px solid ${props => props.theme.body.headers};
    color: ${props => props.theme.body.headers};
`;

const ImageUploadContainer = styled.div`
    margin: 2rem 0;
`;

const ImagePreview = styled.img`
    max-width: 100%;
    max-height: 300px;
    border-radius: 4px;
    margin-top: 1rem;
`;

const ImageUploadButton = styled(Button)`
    background: transparent;
    border: 1px dashed ${props => props.theme.colors.gray};
    color: ${props => props.theme.body.text};
    width: 100%;
    padding: 2rem;
    margin-bottom: 1rem;

    &:hover {
        border-color: ${props => props.theme.body.headers};
    }
`;

const ErrorMessage = styled.p`
    color: ${props => props.theme.colors.error};
    margin-bottom: 1rem;
`;

const WritePost = () => {
    const [title, setTitle] = useState('');
    const [content, setContent] = useState('');
    const [coverImage, setCoverImage] = useState(null);
    const [previewImage, setPreviewImage] = useState('');
    const [error, setError] = useState('');
    const [saving, setSaving] = useState(false);
    const history = useHistory();

    useEffect(() => {
        // Check for authentication
        const token = localStorage.getItem('token');
        if (!token) {
            history.push('/login');
        }
    }, [history]);

    const handleImageUpload = (event) => {
        const file = event.target.files[0];
        if (file) {
            setCoverImage(file);
            const reader = new FileReader();
            reader.onloadend = () => {
                setPreviewImage(reader.result);
            };
            reader.readAsDataURL(file);
        }
    };

    const handlePublish = async (status = 'published') => {
        try {
            setSaving(true);
            setError('');

            const token = localStorage.getItem('token');
            if (!token) {
                throw new Error('Not authenticated');
            }

            const postData = {
                title,
                content,
                status,
                coverImage: previewImage
            };

            const response = await fetch('http://localhost:5000/api/posts', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify(postData)
            });

            const data = await response.json();

            if (response.ok) {
                history.push(`/post/${data._id}`);
            } else {
                throw new Error(data.error || 'Failed to publish post');
            }
        } catch (err) {
            setError(err.message);
        } finally {
            setSaving(false);
        }
    };

    const handleSaveDraft = () => handlePublish('draft');

    if (!localStorage.getItem('token')) {
        return null; // Component will redirect in useEffect
    }

    return (
        <WriteContainer>
            <TitleInput 
                placeholder="Title"
                value={title}
                onChange={(e) => setTitle(e.target.value)}
            />

            <ImageUploadContainer>
                <input
                    type="file"
                    accept="image/*"
                    id="cover-image"
                    hidden
                    onChange={handleImageUpload}
                />
                <ImageUploadButton as="label" htmlFor="cover-image">
                    {previewImage ? 'Change Cover Image' : 'Upload Cover Image'}
                </ImageUploadButton>
                {previewImage && (
                    <ImagePreview src={previewImage} alt="Cover preview" />
                )}
            </ImageUploadContainer>

            <ContentTextarea
                placeholder="Write your story..."
                value={content}
                onChange={(e) => setContent(e.target.value)}
            />

            {error && <ErrorMessage>{error}</ErrorMessage>}

            <ButtonGroup>
                <PublishButton 
                    onClick={() => handlePublish('published')}
                    disabled={saving}
                >
                    {saving ? 'Publishing...' : 'Publish'}
                </PublishButton>
                <SaveDraftButton 
                    onClick={handleSaveDraft}
                    disabled={saving}
                >
                    {saving ? 'Saving...' : 'Save as Draft'}
                </SaveDraftButton>
            </ButtonGroup>
        </WriteContainer>
    );
};

export default WritePost;
