import React from 'react';
import { marked } from 'marked';
import {
    BodyContainer,
    Container,
    Image
} from '../../components/StyledComponents/StyledComponents';
import Navigation from '../../components/Navigation/Navigation.jsx';
import Footer from '../../components/Footer/Footer.jsx';
import ProgressBar from '../../components/ProgressBar/ProgressBar';
import styled from 'styled-components';
import { useNavigate } from 'react-router-dom';

const ImageContainer = styled.div`
    width: 100%;
    max-width: 100%;
    margin: 4rem auto;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;
`;

const ButtonContainer = styled.div`
    display: flex;
    gap: 1rem;
    margin: 2rem 0;
`;

const ActionButton = styled.button`
    padding: 0.8rem 1.5rem;
    border: none;
    border-radius: 4px;
    font-size: 1rem;
    cursor: pointer;
    transition: background-color 0.3s;
    background-color: ${props => props.delete ? '#dc3545' : '#007bff'};
    color: white;

    &:hover {
        background-color: ${props => props.delete ? '#c82333' : '#0056b3'};
    }
`;

class BlogPost extends React.Component {
    state = {
        markdown: '',
        isAdmin: false
    }

    componentDidMount() {
        // Check if user is admin from backend environment variable
        this.setState({ isAdmin: process.env.isAdmin === 'true' });

        fetch(this.props.content)
            .then(response => {
                return response.text()
            })
            .then(text => {
                this.setState({
                    markdown: marked(text)
                })
            })
    }

    handleEdit = () => {
        window.location.href = `/write/${this.props.id}`;
    }

    handleDelete = async () => {
        if (window.confirm('Are you sure you want to delete this post?')) {
            try {
                const response = await fetch(`/api/posts/${this.props.id}`, {
                    method: 'DELETE',
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });

                if (response.ok) {
                    window.location.href = '/';
                } else {
                    throw new Error('Failed to delete post');
                }
            } catch (err) {
                console.error('Error deleting post:', err);
                alert('Failed to delete post');
            }
        }
    }

    render() {
        const { markdown, isAdmin } = this.state;

        return (
            <Container>
                <ProgressBar />
                <Navigation />
                <BodyContainer>
                    <Container small top={12}>
                        <h1>{this.props.title}</h1>
                        <p>{this.props.date}</p>
                        <ImageContainer>
                            <Image src={this.props.image} alt={this.props.title} />
                        </ImageContainer>
                        <div id={`post_${this.props.id}`} dangerouslySetInnerHTML={{__html: markdown}}></div>
                        {isAdmin && (
                            <ButtonContainer>
                                <ActionButton onClick={this.handleEdit}>Edit Post</ActionButton>
                                <ActionButton delete onClick={this.handleDelete}>Delete Post</ActionButton>
                            </ButtonContainer>
                        )}
                    </Container>
                </BodyContainer>
                <Footer />
            </Container>
        )
    }
}

export default BlogPost;
