import React, { Component } from "react";
import ReactDOM from "react-dom";
import { BrowserRouter, Route, Switch, Redirect } from "react-router-dom";
import { ThemeProvider } from "styled-components";
import { AuthProvider } from './context/AuthContext';
import theme from "./components/StyledComponents/Theme";
import { GlobalStyle } from "./components/StyledComponents/StyledComponents.jsx";
import { AnimatePresence } from "framer-motion";

// Pages
import Home from "./pages/Home.jsx";
import Coffee from "./pages/Coffee.jsx";
import Plant from "./pages/Plant.jsx";
import Write from "./pages/Write.jsx";
import Login from "./pages/Login.jsx";
import Blogs from './pages/Blogs';
import Post from './pages/Post';

import BlogPost from './components/BlogPost/BlogPost';

import * as serviceWorker from "./serviceWorker";

// Google Analytics
import ReactGA from "react-ga";

class App extends Component {

    render () {

        if(window.location.hostname !== "localhost") {

            // Google Analytics ID goes here.
            let trackingId = "123";

            ReactGA.initialize(trackingId);
            ReactGA.pageview('/home');
        }


        return (
            <ThemeProvider theme={theme}>
                <GlobalStyle />
                <AuthProvider>
                    <BrowserRouter>
                        <AnimatePresence>
                            <Switch>
                                <Route path="/home">
                                    <Home />
                                </Route>

                                <Route path="/coffee">
                                    <Coffee />
                                </Route>

                                <Route path="/plant">
                                    <Plant />
                                </Route>

                                <Route path="/write">
                                    <Write />
                                </Route>

                                <Route path="/login">
                                    <Login />
                                </Route>

                                <Route path="/blogs">
                                    <Blogs />
                                </Route>

                                <Route path="/post/:id">
                                    <Post />
                                </Route>

                                {Posts.map((post) => (
                                    <Route key={post.route} path={`/${post.route}`}>
                                        <BlogPost
                                            title={post.title}
                                            date={post.date}
                                            image={post.image}
                                            content={post.content}
                                        />
                                    </Route>
                                ))}
                                <Redirect from="/" to="/home" />
                            </Switch>
                        </AnimatePresence>
                    </BrowserRouter>
                </AuthProvider>
            </ThemeProvider>
        )
    }
}

serviceWorker.unregister();
ReactDOM.render(<App />, document.getElementById('root'))
