import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Container } from '../StyledComponents/StyledComponents';
import axios from 'axios';

const PlantImage = styled.img`
    width: 100%;
    max-width: 600px;
    height: auto;
    border-radius: 8px;
    margin: 2rem 0;
    object-fit: cover;
`;

const PlantInfo = styled.div`
    margin: 2rem 0;
    padding: 2rem;
    background-color: ${props => props.theme.colors.lightGray};
    border-radius: 8px;
`;

const PlantDetail = styled.div`
    margin: 1rem 0;
    
    strong {
        color: ${props => props.theme.colors.primary};
    }
`;

const LoadingContainer = styled(Container)`
    text-align: center;
    padding: 2rem;
`;

const ErrorContainer = styled(Container)`
    text-align: center;
    padding: 2rem;
    color: red;
`;

const PlantOfTheDay = () => {
    const [plant, setPlant] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [clientToken, setClientToken] = useState(null);
    const TOKEN = 'YPL0oCPPmylIEwr78weBx3uvF4s3qv2k4YnjfaBC53M';

    // First, get a client-side token
    useEffect(() => {
        const getClientToken = async () => {
            try {
                const params = {
                    origin: window.location.origin,
                    token: TOKEN
                };

                const response = await axios({
                    method: 'post',
                    url: '/api/auth/claim',
                    data: params,
                    headers: { 
                        'Content-Type': 'application/json',
                        'Accept': 'application/json'
                    }
                });

                if (response.data && response.data.token) {
                    setClientToken(response.data.token);
                } else {
                    throw new Error('No token received');
                }
            } catch (err) {
                console.error('Error getting client token:', err);
                setError('Failed to authenticate with the plant database');
                setLoading(false);
            }
        };

        getClientToken();
    }, []);

    // Then fetch the plant data once we have the client token
    useEffect(() => {
        const fetchPlant = async (attemptedIds = new Set()) => {
            if (!clientToken) return;

            try {
                // Get today's date to generate a consistent ID
                const today = new Date().toISOString().split('T')[0];
                const dateNum = parseInt(today.replace(/-/g, ''), 10);
                
                // Use the date to select a plant ID between 1 and 10000
                let plantId = (dateNum % 10000) + 1;

                // If this ID has been attempted, increment until we find one we haven't tried
                while (attemptedIds.has(plantId)) {
                    plantId = (plantId % 10000) + 1;
                }
                attemptedIds.add(plantId);

                const response = await axios.get(
                    `/api/v1/species/${plantId}?token=${clientToken}`,
                    {
                        headers: {
                            'Accept': 'application/json'
                        }
                    }
                );

                if (response.data && response.data.data) {
                    // If the plant has no image and we haven't tried 5 times, try another plant
                    if (!response.data.data.image_url && attemptedIds.size < 5) {
                        return fetchPlant(attemptedIds);
                    }
                    setPlant(response.data.data);
                } else {
                    throw new Error('Invalid plant data received');
                }
                setLoading(false);
            } catch (err) {
                console.error('Error fetching plant:', err);
                // If we haven't tried 5 times and got an error, try another plant
                if (attemptedIds.size < 5) {
                    return fetchPlant(attemptedIds);
                }
                setError(err.message);
                setLoading(false);
            }
        };

        fetchPlant();
    }, [clientToken]);

    if (loading) {
        return (
            <LoadingContainer>
                <h2>Loading today's featured plant...</h2>
            </LoadingContainer>
        );
    }

    if (error) {
        return (
            <ErrorContainer>
                <h2>Error loading plant</h2>
                <p>{error}</p>
            </ErrorContainer>
        );
    }

    if (!plant) {
        return (
            <ErrorContainer>
                <h2>No plant data available</h2>
            </ErrorContainer>
        );
    }

    return (
        <Container>
            <h2>Plant of the Day: {plant.common_name || plant.scientific_name}</h2>
            <h3><em>{plant.scientific_name}</em></h3>
            
            {plant.image_url && (
                <PlantImage src={plant.image_url} alt={plant.common_name || plant.scientific_name} />
            )}
            
            <PlantInfo>
                <PlantDetail>
                    <strong>Family:</strong> {plant.family_common_name}
                </PlantDetail>
                {plant.year && (
                    <PlantDetail>
                        <strong>Year Discovered:</strong> {plant.year}
                    </PlantDetail>
                )}
                {plant.bibliography && (
                    <PlantDetail>
                        <strong>Origin:</strong> {plant.distribution.native}
                    </PlantDetail>
                )}
            </PlantInfo>
        </Container>
    );
};

export default PlantOfTheDay;
