import React, { useState, useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { 
  FeaturedArticlesContainer, 
  FeaturedArticlesTitle, 
  FeaturedArticlesGrid, 
  FeaturedArticleCard, 
  FeaturedArticleImage, 
  FeaturedArticleContent, 
  FeaturedArticleTitle, 
  FeaturedArticleExcerpt, 
  FeaturedArticleDate, 
  FeaturedArticleLink 
} from './FeaturedArticles.styles';

const FeaturedArticles = () => {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const history = useHistory();

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await fetch('http://localhost:5000/api/posts');
        if (!response.ok) {
          throw new Error('Failed to fetch posts');
        }
        const data = await response.json();
        // Sort posts by date and get the latest 3
        const latestPosts = data
          .sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt))
          .slice(0, 3);
        setPosts(latestPosts);
      } catch (err) {
        setError(err.message);
      } finally {
        setLoading(false);
      }
    };

    fetchPosts();
  }, []);

  if (loading) {
    return <div>Loading featured articles...</div>;
  }

  if (error) {
    return <div>Error: {error}</div>;
  }

  return (
    <FeaturedArticlesContainer>
      <FeaturedArticlesTitle>Featured Articles</FeaturedArticlesTitle>
      <FeaturedArticlesGrid>
        {posts.map((post) => (
          <FeaturedArticleCard key={post._id}>
            {post.coverImage && (
              <FeaturedArticleImage 
                src={post.coverImage} 
                alt={post.title}
                onClick={() => history.push(`/post/${post._id}`)}
              />
            )}
            <FeaturedArticleContent>
              <FeaturedArticleTitle onClick={() => history.push(`/post/${post._id}`)}>
                {post.title}
              </FeaturedArticleTitle>
              <FeaturedArticleExcerpt>
                {post.content.substring(0, 150)}...
              </FeaturedArticleExcerpt>
              <FeaturedArticleDate>
                {new Date(post.createdAt).toLocaleDateString()}
              </FeaturedArticleDate>
              <FeaturedArticleLink onClick={() => history.push(`/post/${post._id}`)}>
                Read More
              </FeaturedArticleLink>
            </FeaturedArticleContent>
          </FeaturedArticleCard>
        ))}
      </FeaturedArticlesGrid>
    </FeaturedArticlesContainer>
  );
};

export default FeaturedArticles;
