import React, { useState, useEffect } from 'react';
import { useHistory, useParams } from 'react-router-dom';
import styled from 'styled-components';
import axios from 'axios';

const WriteContainer = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 2rem;
`;

const Title = styled.h1`
  color: ${props => props.theme.colors.primary};
  margin-bottom: 2rem;
`;

const Form = styled.form`
  display: flex;
  flex-direction: column;
  gap: 1.5rem;
`;

const Input = styled.input`
  padding: 0.75rem;
  border: 1px solid ${props => props.theme.colors.gray};
  border-radius: 4px;
  font-size: 1rem;
`;

const TextArea = styled.textarea`
  padding: 0.75rem;
  border: 1px solid ${props => props.theme.colors.gray};
  border-radius: 4px;
  font-size: 1rem;
  min-height: 300px;
  resize: vertical;
`;

const Button = styled.button`
  padding: 0.75rem;
  background-color: #2E7D32;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #1B5E20;
  }

  &:disabled {
    background-color: #A5D6A7;
    cursor: not-allowed;
  }
`;

const ImagePreview = styled.img`
  max-width: 100%;
  max-height: 300px;
  object-fit: contain;
  margin-top: 1rem;
`;

const ErrorMessage = styled.p`
  color: ${props => props.theme.colors.error};
  text-align: center;
`;

const ActionButtons = styled.div`
    display: flex;
    gap: 1rem;
    margin-top: 2rem;
`;

const DeleteButton = styled.button`
    background-color: #ff4444;
    color: white;
    padding: 0.8rem 1.5rem;
    border: none;
    border-radius: 4px;
    font-size: 1rem;
    cursor: pointer;
    transition: background-color 0.3s;

    &:hover {
        background-color: #cc0000;
    }
`;

const ImageUploadContainer = styled.div`
  display: flex;
  align-items: center;
  gap: 1rem;
`;

const ImageUploadLabel = styled.label`
  font-weight: bold;
`;

const ImageUploadInput = styled.input`
  padding: 0.75rem;
  border: 1px solid ${props => props.theme.colors.gray};
  border-radius: 4px;
  font-size: 1rem;
`;

const TitleInput = styled.input`
  padding: 0.75rem;
  border: 1px solid ${props => props.theme.colors.gray};
  border-radius: 4px;
  font-size: 1rem;
`;

const ContentTextarea = styled.textarea`
  padding: 0.75rem;
  border: 1px solid ${props => props.theme.colors.gray};
  border-radius: 4px;
  font-size: 1rem;
  min-height: 300px;
  resize: vertical;
`;

const PublishButton = styled.button`
  padding: 0.75rem;
  background-color: #2E7D32;
  color: white;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: #1B5E20;
  }

  &:disabled {
    background-color: #A5D6A7;
    cursor: not-allowed;
  }
`;

const WritePost = () => {
  const [title, setTitle] = useState('');
  const [content, setContent] = useState('');
  const [coverImage, setCoverImage] = useState(null);
  const [previewUrl, setPreviewUrl] = useState('');
  const [error, setError] = useState('');
  const [isEditing, setIsEditing] = useState(false);
  const [postId, setPostId] = useState(null);
  const history = useHistory();  const { id } = useParams();

  useEffect(() => {
    if (id) {
      // If we have an ID, we're in edit mode
      setIsEditing(true);
      setPostId(id);
      fetchPost(id);
    }
  }, [id]);

  const fetchPost = async (postId) => {
    try {
      const token = localStorage.getItem('token');
      const response = await axios.get(`/api/posts/${postId}`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      const post = response.data;
      setTitle(post.title);
      setContent(post.content);
      if (post.coverImage) {
        setPreviewUrl(post.coverImage);
      }
    } catch (err) {
      setError('Failed to fetch post');
      console.error('Error fetching post:', err);
    }
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setCoverImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setPreviewUrl(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    try {
      const token = localStorage.getItem('token');
      const formData = new FormData();
      formData.append('title', title);
      formData.append('content', content);
      if (coverImage) {
        formData.append('coverImage', coverImage);
      }

      if (isEditing) {
        // Update existing post
        await axios.put(`/api/posts/${postId}`, formData, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        });
      } else {
        // Create new post
        await axios.post('/api/posts', formData, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'multipart/form-data'
          }
        });
      }
      history.push('/');
    } catch (err) {
      setError('Failed to save post');
      console.error('Error saving post:', err);
    }
  };

  const handleDelete = async () => {
    if (window.confirm('Are you sure you want to delete this post?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`/api/posts/${postId}`, {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });
        history.push('/');
      } catch (err) {
        setError('Failed to delete post');
        console.error('Error deleting post:', err);
      }
    }
  };

  return (
    <WriteContainer>
      <Title>Write a New Post</Title>
      <Form onSubmit={handleSubmit}>
        <TitleInput
          type="text"
          placeholder="Title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          required
        />
        
        <ImageUploadContainer>
          <ImageUploadLabel>
            Choose Cover Image
            <ImageUploadInput
              type="file"
              accept="image/*"
              onChange={handleImageChange}
            />
          </ImageUploadLabel>
          {previewUrl && (
            <ImagePreview src={previewUrl} alt="Preview" />
          )}
        </ImageUploadContainer>

        <ContentTextarea
          placeholder="Write your post here..."
          value={content}
          onChange={(e) => setContent(e.target.value)}
          required
        />

        {error && <ErrorMessage>{error}</ErrorMessage>}

        <ActionButtons>
          <PublishButton type="submit">
            {isEditing ? 'Update Post' : 'Publish Post'}
          </PublishButton>
          {isEditing && (
            <DeleteButton type="button" onClick={handleDelete}>
              Delete Post
            </DeleteButton>
          )}
        </ActionButtons>
      </Form>
    </WriteContainer>
  );
};

export default WritePost;
