import React, { useState, useEffect } from 'react';
import { Container, Image } from '../StyledComponents/StyledComponents';
import drinksData from '../../data/drinksData';
import styled from 'styled-components';

const DrinkContainer = styled(Container)`
    text-align: center;
    max-width: 600px;
    margin: 0 auto;
    padding: 2rem;
`;

const RecipeSection = styled.div`
    margin: 2rem 0;
    text-align: left;
`;

const IngredientsList = styled.ul`
    list-style-type: none;
    padding: 0;
    margin: 1rem 0;
`;

const StepList = styled.ol`
    padding-left: 1.5rem;
    margin: 1rem 0;
`;

const VariationList = styled.ul`
    list-style-type: none;
    padding: 0;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 1rem;
    margin: 1rem 0;
`;

const VariationItem = styled.li`
    background: rgba(255, 255, 255, 0.1);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-size: 0.9rem;
`;

const NavigationButton = styled.button`
    background: rgba(255, 255, 255, 0.1);
    border: none;
    color: white;
    padding: 0.5rem 1rem;
    border-radius: 20px;
    margin: 0 0.5rem;
    cursor: pointer;
    transition: background 0.3s ease;

    &:hover {
        background: rgba(255, 255, 255, 0.2);
    }
`;

const CoffeeOfTheDay = () => {
    const [currentDrinkIndex, setCurrentDrinkIndex] = useState(0);
    const currentDrink = drinksData[currentDrinkIndex];

    const nextDrink = () => {
        setCurrentDrinkIndex((prevIndex) => 
            prevIndex === drinksData.length - 1 ? 0 : prevIndex + 1
        );
    };

    const prevDrink = () => {
        setCurrentDrinkIndex((prevIndex) => 
            prevIndex === 0 ? drinksData.length - 1 : prevIndex - 1
        );
    };

    return (
        <DrinkContainer>
            <Image src={currentDrink.image} alt={currentDrink.name} />
            <h2>{currentDrink.name}</h2>
            <p>{currentDrink.description}</p>
            
            <RecipeSection>
                <h3>Ingredients</h3>
                <IngredientsList>
                    {currentDrink.recipe.ingredients.map((ingredient, index) => (
                        <li key={index}>{ingredient}</li>
                    ))}
                </IngredientsList>

                <h3>Steps</h3>
                <StepList>
                    {currentDrink.recipe.steps.map((step, index) => (
                        <li key={index}>{step}</li>
                    ))}
                </StepList>
            </RecipeSection>

            <h3>Tasting Notes</h3>
            <VariationList>
                {currentDrink.tastingNotes.map((note, index) => (
                    <VariationItem key={index}>{note}</VariationItem>
                ))}
            </VariationList>

            <h3>Variations</h3>
            <VariationList>
                {currentDrink.variations.map((variation, index) => (
                    <VariationItem key={index}>{variation}</VariationItem>
                ))}
            </VariationList>

            <p>Origin: {currentDrink.origin}</p>

            <div style={{ marginTop: '2rem' }}>
                <NavigationButton onClick={prevDrink}>Previous</NavigationButton>
                <NavigationButton onClick={nextDrink}>Next</NavigationButton>
            </div>
        </DrinkContainer>
    );
};

export default CoffeeOfTheDay;
