import React, { useState, useEffect } from 'react';
import styled from 'styled-components';
import { Container, Image, Title, Description, List, ListItem } from '../StyledComponents/StyledComponents';
import coffeeData from '../../data/coffeeData';

const DrinkContainer = styled(Container)`
    background-color: ${props => props.theme.colors.white};
    padding: 2rem;
    border-radius: 8px;
    box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
    margin: 2rem auto;
    max-width: 800px;
`;

const CoffeeOfTheDay = () => {
    const [drink, setDrink] = useState(null);

    useEffect(() => {
        // Get today's date to generate a consistent random number
        const today = new Date().toISOString().split('T')[0];
        const dateNum = parseInt(today.replace(/-/g, ''), 10);
        
        // Use the date to select a drink
        const drinkIndex = dateNum % coffeeData.length;
        setDrink(coffeeData[drinkIndex]);
    }, []);

    if (!drink) {
        return <div>Loading...</div>;
    }

    return (
        <DrinkContainer>
            <Title>{drink.name}</Title>
            <Image src={drink.image} alt={drink.name} />
            <Description>{drink.description}</Description>
            <List>
                <h3>Ingredients:</h3>
                {drink.ingredients.map((ingredient, index) => (
                    <ListItem key={index}>{ingredient}</ListItem>
                ))}
            </List>
            <List>
                <h3>Steps:</h3>
                {drink.steps.map((step, index) => (
                    <ListItem key={index}>{step}</ListItem>
                ))}
            </List>
        </DrinkContainer>
    );
};

export default CoffeeOfTheDay;
