import React from 'react';
import { Container, Image } from '../StyledComponents/StyledComponents';
import coffeeData from '../../data/coffeeData';
import styled from 'styled-components';

const DrinkContainer = styled(Container)`
    text-align: center;
    max-width: 600px;
    margin: 0 auto;
    padding: 2rem;
`;

const RecipeSection = styled.div`
    margin: 2rem 0;
    text-align: left;
`;

const IngredientsList = styled.ul`
    list-style-type: none;
    padding: 0;
    margin: 1rem 0;
`;

const StepList = styled.ol`
    padding-left: 1.5rem;
    margin: 1rem 0;
`;

const VariationList = styled.ul`
    list-style-type: none;
    padding: 0;
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    gap: 1rem;
    margin: 1rem 0;
`;

const VariationItem = styled.li`
    background: rgba(255, 255, 255, 0.1);
    padding: 0.5rem 1rem;
    border-radius: 20px;
    font-size: 0.9rem;
`;

const CoffeeOfTheDay = () => {
    return (
        <DrinkContainer>
            <Image src={coffeeData.image} alt={coffeeData.name} />
            <h2>{coffeeData.name}</h2>
            <p>{coffeeData.description}</p>
            
            <RecipeSection>
                <h3>Ingredients</h3>
                <IngredientsList>
                    {coffeeData.recipe.ingredients.map((ingredient, index) => (
                        <li key={index}>{ingredient}</li>
                    ))}
                </IngredientsList>

                <h3>Steps</h3>
                <StepList>
                    {coffeeData.recipe.steps.map((step, index) => (
                        <li key={index}>{step}</li>
                    ))}
                </StepList>
            </RecipeSection>

            <h3>Tasting Notes</h3>
            <VariationList>
                {coffeeData.tastingNotes.map((note, index) => (
                    <VariationItem key={index}>{note}</VariationItem>
                ))}
            </VariationList>

            <h3>Variations</h3>
            <VariationList>
                {coffeeData.variations.map((variation, index) => (
                    <VariationItem key={index}>{variation}</VariationItem>
                ))}
            </VariationList>

            <p>Origin: {coffeeData.origin}</p>
        </DrinkContainer>
    );
};

export default CoffeeOfTheDay;
