import React, { useState, useEffect } from 'react';
import { useParams, useHistory } from 'react-router-dom';
import styled from 'styled-components';
import { Container } from '../components/StyledComponents/StyledComponents';
import axios from 'axios';
import Navigation from '../components/Navigation/Navigation';
import Footer from '../components/Footer/Footer';

const PostContainer = styled(Container)`
    padding-top: 100px;
    max-width: 800px;
    margin: 0 auto;
`;

const PostTitle = styled.h1`
    color: ${props => props.theme.colors.text};
    margin-bottom: 2rem;
    text-align: center;
`;

const PostImage = styled.img`
    width: 100%;
    max-width: 600px;
    height: auto;
    border-radius: 8px;
    margin: 2rem auto;
    display: block;
`;

const PostContent = styled.div`
    color: ${props => props.theme.colors.text};
    line-height: 1.6;
    font-size: 1.1rem;
    margin-bottom: 2rem;
    white-space: pre-wrap;
`;

const PostMeta = styled.div`
    color: ${props => props.theme.colors.text};
    font-size: 0.9rem;
    margin-bottom: 2rem;
    text-align: center;
`;

const AdminButtons = styled.div`
    display: flex;
    gap: 1rem;
    justify-content: center;
    margin-top: 2rem;
`;

const Button = styled.button`
    padding: 0.8rem 1.5rem;
    border: none;
    border-radius: 4px;
    font-size: 1rem;
    cursor: pointer;
    transition: background-color 0.3s;
    color: white;

    &:hover {
        opacity: 0.9;
    }
`;

const EditButton = styled(Button)`
    background-color: ${props => props.theme.colors.primary};
`;

const DeleteButton = styled(Button)`
    background-color: #dc3545;
`;

const Post = () => {
    const { id } = useParams();
    const history = useHistory();
    const [post, setPost] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const isAdmin = localStorage.getItem('isAdmin') === 'true';

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await axios.get(`/api/posts/${id}`);
                setPost(response.data);
                setLoading(false);
            } catch (err) {
                setError('Failed to load post');
                setLoading(false);
            }
        };

        fetchPost();
    }, [id]);

    const handleEdit = () => {
        history.push(`/write/${id}`);
    };

    const handleDelete = async () => {
        if (window.confirm('Are you sure you want to delete this post?')) {
            try {
                await axios.delete(`/api/posts/${id}`, {
                    headers: {
                        'Authorization': `Bearer ${localStorage.getItem('token')}`
                    }
                });
                history.push('/');
            } catch (err) {
                setError('Failed to delete post');
            }
        }
    };

    if (loading) return <PostContainer>Loading...</PostContainer>;
    if (error) return <PostContainer>{error}</PostContainer>;
    if (!post) return <PostContainer>Post not found</PostContainer>;

    return (
        <>
            <Navigation />
            <PostContainer>
                <PostTitle>{post.title}</PostTitle>
                {post.coverImage && (
                    <PostImage src={post.coverImage} alt={post.title} />
                )}
                <PostMeta>
                    Published on {new Date(post.createdAt).toLocaleDateString()}
                </PostMeta>
                <PostContent>{post.content}</PostContent>

                {isAdmin && (
                    <AdminButtons>
                        <EditButton onClick={handleEdit}>Edit Post</EditButton>
                        <DeleteButton onClick={handleDelete}>Delete Post</DeleteButton>
                    </AdminButtons>
                )}
            </PostContainer>
            <Footer />
        </>
    );
};

export default Post;
